catalog_ui_policy.script_true:
function onCondition() {
    g_form.getReference('request_by_full_name', populateRequestInstance);

    function populateRequestInstance(user) {
        if (user.email != '') {
            g_form.setValue('requested_for_email_address', user.email);
        } else {
            g_form.clearValue('requested_for_email_address');
        }
        if (user.phone != '') {
            g_form.setValue('requested_for_phone_number', user.phone);
        } else {
            g_form.clearValue('requested_for_phone_number');
        }
        if (user.location != '') {
            g_form.setValue('requested_for_location', user.location);
        } else {
            g_form.clearValue('requested_for_location');
        }
        // 	if(user.office_symbol != ''){
        // 			g_form.setValue('requested_for_office_symbol', user.office_symbol);
        // 		}
        // 		else{
        // 			g_form.clearValue('requested_for_office_symbol');
        // 		}
    }
}
